cleaned_html_content = html_content.replace('""', '"')


def remove_attributes_except_a(soup):

    for img_tag in soup.find_all('img'):
        img_tag.decompose()

    for style_tag in soup.find_all('style'):
        style_tag.decompose()
    
    # Replace iframe tags with <a> tags
    for iframe_tag in soup.find_all('iframe'):
        video_url = iframe_tag['src']
        print("video url is : ",video_url)
        new_a_tag = soup.new_tag('a', href=video_url, target="_blank")
        new_a_tag.string = "Click here to watch video"
        iframe_tag.replace_with(new_a_tag)

    for tag in soup.find_all(True):
        if tag.name != "a":
            tag.attrs = {}

    
    cleaned_html = str(soup)
            
    return cleaned_html

soup = BeautifulSoup(cleaned_html_content, 'html.parser')
soup = remove_attributes_except_a(soup)


print(soup)
